import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { MatIconModule } from '@angular/material/icon';
import { Router, NavigationEnd } from '@angular/router';
import { AuthService } from 'app/Service/auth/auth.service';
import { filter } from 'rxjs/operators';

interface NavItem {
  name: string;
  icon: string;
  route?: string;
  children?: NavItem[];
  expanded?: boolean;
}

@Component({
  imports: [CommonModule, MatIconModule],
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css'],
})
export class SidebarComponent implements OnInit {
  tabs: NavItem[] = [];
  userRole!: string;
  activeIndex = 0; // Tracks the active parent tab
  activeChildIndex: number | null = null; // Tracks the active child tab (null if no child is active)

  constructor(
    private router: Router,
    private authService: AuthService,
  ) {
    this.router.events
      .pipe(filter((event) => event instanceof NavigationEnd))
      .subscribe(() => {
        this.updateActiveTab();
      });
  }

  ngOnInit(): void {
    this.userRole = this.authService.getRoles();
    this.setNavItemsBasedOnRole(this.userRole);
  }

  /**
   * Set the active tab (parent or child) and navigate to its route.
   * @param parentIndex - The index of the parent tab.
   * @param childIndex - The index of the child tab (optional).
   */
  setActive(parentIndex: number, childIndex?: number): void {
    const tab = this.tabs[parentIndex];

    // If a child index is provided, handle child tab activation
    if (childIndex !== undefined && tab.children) {
      this.activeIndex = parentIndex;
      this.activeChildIndex = childIndex;
      const childTab = tab.children[childIndex];
      if (childTab.route) {
        this.router.navigate([childTab.route]);
      }
      tab.expanded = true; // Ensure parent is expanded
      return;
    }

    // Handle parent tab activation
    this.activeIndex = parentIndex;
    this.activeChildIndex = null; // Reset child index when a parent tab is clicked

    if (tab.children) {
      this.toggleExpand(parentIndex);
      return;
    }

    if (tab.route) {
      this.router.navigate([tab.route]);
    }
  }

  /**
   * Toggle the expanded state of a tab with children.
   * @param index - The index of the tab to expand/collapse.
   */
  toggleExpand(index: number): void {
    const tab = this.tabs[index];
    if (tab.children) {
      tab.expanded = !tab.expanded;
    }
  }

  /**
   * Expose the number of tabs for CSS calculations.
   */
  get tabCount(): number {
    return this.tabs.length;
  }

  /**
   * Set navigation items based on the user's role.
   */
  private setNavItemsBasedOnRole(role: string | null): void {
    switch (role) {
      case 'MANAGER':
        this.tabs = [
          { name: 'Dashboard', icon: 'dashboard', route: '/staff/dashboard' },
          { name: 'Customers', icon: 'people', route: '/staff/dashboard' },
          {
            name: 'Suppliers',
            icon: 'local_shipping',
            route: '/staff/suppliers',
          },
          {
            name: 'Analytics',
            icon: 'analytics',
            expanded: false,
            children: [
              {
                name: 'Customers',
                icon: 'people',
                route: '/staff/analytics/customers',
              },
              {
                name: 'Suppliers',
                icon: 'local_shipping',
                route: '/staff/analytics/suppliers',
              },
              {
                name: 'Staff',
                icon: 'groups',
                route: '/staff/analytics/staff',
              },
              {
                name: 'Sales',
                icon: 'trending_up',
                route: '/staff/analytics/sales',
              },
            ],
          },
          {
            name: 'Discounts & Coupons',
            icon: 'local_offer',
            route: '/staff/discounts',
          },
          {
            name: 'Notifications',
            icon: 'notifications_active',
            route: '/staff/notifications',
          },
          { name: 'Settings', icon: 'settings', route: '/admin/settings' },
        ];
        break;

      case 'ADMIN':
        this.tabs = [
          { name: 'Dashboard', icon: 'dashboard', route: '/admin/dashboard' },
          { name: 'Customers', icon: 'people', route: '/admin/customers' },
          {
            name: 'Loyalty Program',
            icon: 'card_giftcard',
            route: '/admin/loyalty',
          },
          {
            name: 'Coupons Management',
            icon: 'local_offer',
            route: '/admin/coupons',
          },
          {
            name: 'Notifications',
            icon: 'notifications_active',
            route: '/admin/notifications',
          },
          { name: 'Settings', icon: 'settings', route: '/admin/settings' },
        ];
        break;

      case 'STAFF':
        this.tabs = [
          { name: 'Dashboard', icon: 'dashboard', route: '/staff/dashboard' },
          {
            name: 'Inventory',
            icon: 'inventory_2',
            expanded: false,
            children: [
              {
                name: 'Products',
                icon: 'shopping_bag',
                route: '/staff/inventory/products',
              },
              {
                name: 'Category',
                icon: 'category',
                route: '/staff/inventory/category',
              },
              {
                name: 'Brands',
                icon: 'store_mall_directory',
                route: '/staff/inventory/brands',
              },
              {
                name: 'Discounts',
                icon: 'local_offer',
                route: '/staff/inventory/discounts',
              },
              {
                name: 'Expiry',
                icon: 'event_available',
                route: '/staff/inventory/expiry',
              },
              {
                name: 'Low Stock',
                icon: 'inventory_2',
                route: '/staff/inventory/low-stock',
              },
            ],
          },
          {
            name: 'Notifications',
            icon: 'notifications_active',
            route: '/staff/notifications',
          },
          { name: 'Settings', icon: 'settings', route: '/admin/settings' },
        ];
        break;

      default:
        this.tabs = [];
        break;
    }
  }

  /**
   * Update the active tab based on the current route.
   */
  private updateActiveTab(): void {
    const currentRoute = this.router.url;

    this.tabs.forEach((tab, parentIndex) => {
      if (tab.route === currentRoute) {
        this.activeIndex = parentIndex;
        this.activeChildIndex = null;
        return;
      }
      if (tab.children) {
        const childIndex = tab.children.findIndex(
          (child) => child.route === currentRoute,
        );
        if (childIndex !== -1) {
          this.activeIndex = parentIndex;
          this.activeChildIndex = childIndex;
          tab.expanded = true;
        }
      }
    });
  }

  /**
   * Log out the user and navigate to the login page.
   */
  logout(): void {
    this.authService.logout();
    this.router.navigate(['/login'], { replaceUrl: true });
  }
}

