import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class ApiService {

  constructor() { }
}

import { Injectable } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';
import { BehaviorSubject, Observable, tap, throwError } from 'rxjs';
import { CookieService } from 'ngx-cookie-service';
import { decodeToken, isTokenExpired } from 'app/utils/jwt.utils';
import { ToastService } from 'app/Service/toast/toast.service';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  public isLoggedInSubject = new BehaviorSubject<boolean>(false);
  isLoggedIn$ = this.isLoggedInSubject.asObservable();
  defaultAvatar = 'assets/default-avatar.png';

  // New BehaviorsSubject to store our data
  private userSubject = new BehaviorSubject<any>(null);
  user$ = this.userSubject.asObservable();

  private apiUrl = '/user';
  constructor(
    private http: HttpClient,
    private cookieService: CookieService,
    private toastService: ToastService,
  ) {
    // Check if token exists on app initialization
    const token = this.getToken();
    if (token && !isTokenExpired(token)) {
      this.isLoggedInSubject.next(true);
      this.setUserFromToken(token);
    }
  }

  login(username: string, password: string): Observable<any> {
    return this.http
      .post(
        `${this.apiUrl}/login
        `,
        { username, password },
        { withCredentials: true },
      )
      .pipe(
        tap((response: any) => {
          const token = response.data.token; // Assuming backend returns { token: 'jwt-token' }
          if (token) {
            // console.log('Token received from backend:', token);
            this.setTokenInCookie(token); // Store token securely in a cookie
            this.setUserFromToken(token);
            this.isLoggedInSubject.next(true);
          } else {
            // console.error('No token found in response');
            this.toastService.showError('Invalid username or password');
          }
        }),
      );
  }

  getToken(): string | null {
    return this.cookieService.get('token'); // Retrieve token from cookies
  }
  logout() {
    this.cookieService.delete('token', '/'); // Remove token from cookies
    this.isLoggedInSubject.next(false);
    this.userSubject.next(null); // Clear user data
  }
  isLoggedIn(): boolean {
    const token = this.getToken();
    return !!token && !isTokenExpired(token);
  }
  getUserName(): string {
    const token = this.getToken();
    if (!token) return 'Guest';

    const decodedToken = decodeToken(token);
    return decodedToken?.sub || 'Guest'; // Extract name from token claims
  }
  getRoles(): string {
    const token = this.getToken();
    if (!token) return 'guest';

    const decodedToken = decodeToken(token);
    return decodedToken?.role || 'guest'; // Extract roles from token claims
  }
  setRoles(roles: string[]): void {
    this.cookieService.set('role', JSON.stringify(roles));
  }
  private setTokenInCookie(token: string): void {
    this.cookieService.set(
      'token', // Name of the cookie
      token, // Value of the cookie (JWT token)
      1, // Expiration time in days
      '/', // Path (accessible across the entire app)
      undefined, // Domain (optional)
      false, // Secure flag (only send over HTTPS)
      'Lax', // SameSite policy ('Strict', 'Lax', or 'None')
    );
  }
  // Method to extract user data from token and update userSubject
  public setUserFromToken(token: string): void {
    const decodedToken = decodeToken(token);
    const userData = {
      username: decodedToken?.sub || 'Guest', // Extract name from token
      avatar: decodedToken?.avatar || this.defaultAvatar, // Extract avatar URL or use default
      role: decodedToken?.role || 'user',
    };
    this.userSubject.next(userData); // Update user data
  }

  verifyOtp(otpData: any, otp: string): Observable<any> {
    const params = new HttpParams().set('otp', otp);
    return this.http
      .post(`${this.apiUrl}/verify-otp`, otpData, { params })
      .pipe(
        tap((response: any) => {
          if (response.status === 'success') {
            // console.log('otp verified successfully');
            this.toastService.showSuccess(
              'You have been successfully registered! Please Login again',
            );
          } else {
            this.toastService.showError('Invalid OTP');
          }
        }),
      );
  }
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { firstValueFrom } from 'rxjs';
import { ToastService } from 'app/Service/toast/toast.service';
import { CartItems } from 'app/customer/Model/cart/cart-items';
import { CartItemDisplay } from 'app/customer/Model/cart/cart-item-display';

@Injectable({
  providedIn: 'root',
})
export class CartService {
  private apiUrl = '/product/search';
  private discountApiUrl = '/discount/active';
  private cartItems: CartItems[] = [];
  private cartItemsSubject = new BehaviorSubject<CartItemDisplay[]>([]);
  cartItems$ = this.cartItemsSubject.asObservable();

  constructor(
    private http: HttpClient,
    private toastService: ToastService,
  ) {
    this.loadCartFromStorage();
  }

  private loadCartFromStorage(): void {
    const storedCart = JSON.parse(sessionStorage.getItem('cart') || '[]');
    this.cartItems = storedCart;
    this.updateCartItemsWithDiscounts();
  }

  private saveCart(cart: CartItems[]): void {
    this.cartItems = cart;
    sessionStorage.setItem('cart', JSON.stringify(cart));

    this.updateCartItemsWithDiscounts().then(() => {
      const { totalAmount, originalTotal } = this.calculateCartTotals();
      const totalQuantity = this.getTotalItemCounts();

      sessionStorage.setItem('totalAmount', totalAmount.toString());
      sessionStorage.setItem('originalTotal', originalTotal.toString());
      sessionStorage.setItem('totalQuantity', totalQuantity.toString());
    });
  }

  getCartItems(): CartItems[] {
    return [...this.cartItems];
  }

  getCartItemsWithDiscounts(): CartItemDisplay[] {
    return this.cartItemsSubject.value;
  }

  async addToCart(product: any, quantity: number): Promise<void> {
    if (quantity <= 0 || quantity > product.stockUnit) {
      this.toastService.showWarning(
        'Invalid quantity. Please select a valid amount.',
      );
      return;
    }

    const isValid = await this.validateStock(product.barcode, quantity);
    if (!isValid) {
      this.toastService.showWarning('Stock Not Available!');
      return;
    }

    const cart = this.getCartItems();
    const existingItemIndex = cart.findIndex(
      (item) => item.barcode === product.barcode,
    );

    if (existingItemIndex !== -1) {
      const newQuantity = cart[existingItemIndex].cartQuantity + quantity;
      if (newQuantity > product.stockUnit) {
        this.toastService.showWarning('Cannot exceed available stock!');
        return;
      }
      cart[existingItemIndex].cartQuantity = newQuantity;
    } else {
      cart.push({ ...product, cartQuantity: quantity });
    }

    this.saveCart(cart);
    this.toastService.showSuccess('Item added to cart!');
  }

  async validateStock(barcode: string, quantity: number): Promise<boolean> {
    try {
      const isValid = await firstValueFrom(
        this.http.get<boolean>(`${this.apiUrl}/validate-stock`, {
          params: { barcode, quantity: quantity.toString() },
        }),
      );
      return isValid;
    } catch (error) {
      console.error('Error validating stock:', error);
      return false;
    }
  }

  removeFromCart(barcode: string): void {
    const cart = this.getCartItems().filter((item) => item.barcode !== barcode);
    this.saveCart(cart);
    this.toastService.showSuccess('Item removed from cart!');
  }

  clearCart(): void {
    this.saveCart([]);
    this.toastService.showSuccess('Cart cleared!');
  }

  async updateCartItemQuantity(
    barcode: string,
    quantity: number,
  ): Promise<void> {
    const cart = this.getCartItems();
    const itemIndex = cart.findIndex((item) => item.barcode === barcode);

    if (itemIndex === -1) {
      this.toastService.showWarning('Item not found in the cart!');
      return;
    }

    if (quantity <= 0) {
      this.removeFromCart(barcode);
      return;
    }

    const isValid = await this.validateStock(barcode, quantity);
    if (!isValid) {
      this.toastService.showWarning('Stock Not Available!');
      return;
    }

    cart[itemIndex].cartQuantity = quantity;
    this.saveCart(cart);
    // this.toastService.showSuccess('Cart updated!');
  }

  async fetchActiveDiscount(barcodes: string[]): Promise<any> {
    try {
      const discounts = await firstValueFrom(
        this.http.get<any>(this.discountApiUrl, {
          params: { barcodes: barcodes.join(',') },
        }),
      );
      return discounts;
    } catch (error) {
      console.error('Error fetching discounts:', error);
      return { data: [] };
    }
  }

  private async updateCartItemsWithDiscounts(): Promise<void> {
    const rawCartItems = this.getCartItems();
    if (!rawCartItems.length) {
      this.cartItemsSubject.next([]);
      return;
    }

    const barcodes = rawCartItems.map((item) => item.barcode);
    const discountResponse = await this.fetchActiveDiscount(barcodes);
    const discounts = discountResponse.data;

    const discountMap = new Map<string, any>();
    discounts.forEach((discountObj: any) => {
      discountMap.set(discountObj.barcode, discountObj.discount);
    });

    const transformedItems = rawCartItems.map((item) => {
      const discount = discountMap.get(item.barcode);
      const hasDiscount = !!discount;
      const discountedPrice = this.calculateDiscountedPrice(item, discount);
      return {
        productName: item.productName,
        price: item.price || 0,
        cartQuantity: item.cartQuantity || 1,
        currency: item.currency || 'USD',
        hasDiscount,
        discount,
        prodImgURL: item.prodImgURL || null,
        barcode: item.barcode,
        discountedPrice,
      } as CartItemDisplay;
    });

    this.cartItemsSubject.next(transformedItems);
  }

  private calculateDiscountedPrice(item: CartItems, discount?: any): number {
    let price = item.price || 0;
    if (discount) {
      if (discount.isPercentage) {
        price -= price * ((discount.discountValue || 0) / 100);
      } else {
        price -= discount.discountValue || 0;
      }
    }
    return Math.max(0, price);
  }

  calculateCartTotals(): { totalAmount: number; originalTotal: number } {
    const items = this.getCartItemsWithDiscounts();
    return items.reduce(
      (acc, item) => {
        const discountedPrice = item.discountedPrice || 0;
        const originalPrice = item.price || 0;
        const quantity = item.cartQuantity || 1;
        acc.totalAmount += discountedPrice * quantity;
        acc.originalTotal += originalPrice * quantity;
        return acc;
      },
      { totalAmount: 0, originalTotal: 0 },
    );
  }

  getTotalItemCounts(): number {
    return this.cartItems.reduce(
      (sum, item) => sum + (item.cartQuantity || 1),
      0,
    );
  }
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { CartService } from '../cartService/cart.service';
import { ToastService } from 'app/Service/toast/toast.service';
import { firstValueFrom } from 'rxjs';
import { AuthService } from 'app/Service/auth/auth.service';
import { isTokenExpired } from 'app/utils/jwt.utils';
import { Router } from '@angular/router';
import { CheckoutData } from 'app/customer/Model/checkOut';

interface CheckoutResponse {
  status: string;
  message: string;
  data: CheckoutData;
}
interface CancelResponse {
  status: string;
  message: string;
  data: string; // Based on your backend signature: ApiResponseWrapper<String>
}
@Injectable({
  providedIn: 'root',
})
export class CheckoutService {
  private baseUrl = '/v1';
  private couponUrl = '/coupon';
  private TAX_RATE = 0.3;

  constructor(
    private http: HttpClient,
    private cartService: CartService,
    private toastService: ToastService,
    private authService: AuthService,
    private router: Router,
  ) {}
  // loyalty points
  async fetchLoyaltyPoints(username: string): Promise<number | null> {
    const token = this.authService.getToken();
    if (!token || isTokenExpired(token)) {
      console.warn('No valid token for loyalty points fetch.');
      throw new Error('User not authenticated');
    }

    try {
      const points = await firstValueFrom(
        this.http.get<number>(
          `${this.baseUrl}/purchase/${username}/loyaltyPoints`,
        ),
      );
      return points || 0;
    } catch (error: any) {
      if (error.status === 404) {
        return null; // No loyalty points record exists
      }
      console.error('Error fetching loyalty points:', error);
      this.toastService.showError('Failed to fetch loyalty points');
      throw error; // Re-throw for other errors
    }
  }
  // coupon
  async applyCoupon(couponCode: string, totalAmount: number): Promise<number> {
    if (!couponCode) {
      this.toastService.showError('Please enter a valid coupon code');
      return 0;
    }

    try {
      const response: any = await firstValueFrom(
        this.http.post(`${this.couponUrl}/apply`, {
          couponCode,
          totalAmount: totalAmount.toString(),
        }),
      );
      if (response.status === 'success') {
        const discount = parseFloat(response.data) || 0;
        return discount;
      } else {
        this.toastService.showError(
          response.message || 'Failed to apply coupon',
        );
        return 0;
      }
    } catch (error) {
      console.error('Error applying coupon:', error);
      this.toastService.showError('Failed to apply coupon');
      return 0;
    }
  }

  // validate loyalty points
  validateLoyaltyPoints(
    points: number,
    availablePoints: number | null,
  ): number {
    const effectivePoints = availablePoints ?? 0; // Treat null as 0
    if (points > effectivePoints) {
      this.toastService.showWarning('Insufficient loyalty points.');
      return 0;
    } else if (points < 0) {
      this.toastService.showWarning('Points cannot be negative.');
      return 0;
    } else {
      return points; // 1 point = 1 USD
    }
  }
  // calculate totals
  calculateTotals(
    originalTotal: number,
    cartTotal: number,
    couponDiscount: number,
    loyaltyDiscount: number,
  ): { taxAmount: number; grandTotal: number } {
    const subtotal = cartTotal - couponDiscount - loyaltyDiscount;
    const taxAmount = subtotal * this.TAX_RATE;
    const grandTotal = subtotal + taxAmount;
    return { taxAmount, grandTotal: grandTotal < 0 ? 0 : grandTotal };
  }

  // initiate purchase
  async initiatePurchase(
    username: string,
    couponCode: string | null,
    loyaltyPointsUsed: number,
  ): Promise<CheckoutResponse> {
    const cartItems = this.cartService.getCartItemsWithDiscounts();
    const totals = this.cartService.calculateCartTotals();
    const couponDiscount = couponCode
      ? await this.applyCoupon(couponCode, totals.totalAmount)
      : 0;
    const availablePoints = await this.fetchLoyaltyPoints(username);
    const loyaltyDiscount = this.validateLoyaltyPoints(
      loyaltyPointsUsed,
      availablePoints,
    );
    const { taxAmount, grandTotal } = this.calculateTotals(
      totals.originalTotal,
      totals.totalAmount,
      couponDiscount,
      loyaltyDiscount,
    );

    const checkoutData: CheckoutData = {
      cartItems: cartItems.map((item) => ({
        barcode: item.barcode,
        name: item.productName,
        price: item.price,
        discountedPrice: item.discountedPrice,
        quantity: item.cartQuantity,
      })),
      originalAmount: totals.originalTotal,
      totalAmount: totals.totalAmount, // After discount
      couponCode: couponCode || null,
      couponDiscount: couponDiscount || 0,
      loyaltyPointsUsed: loyaltyPointsUsed || 0,
      loyaltyDiscount: loyaltyDiscount || 0,
      taxAmount,
      finalAmount: grandTotal,
      username,
    };
    sessionStorage.setItem('checkoutData', JSON.stringify(checkoutData));
    const token = this.authService.getToken();
    if (!token) {
      this.toastService.showError('Token is missing. Please log in again.');
      this.router.navigate(['/login']);
      throw new Error('Token is missing. Please log in again.');
    }
    // document.cookie = `authToken=${token}; path=/; SameSite=Lax`;
    try {
      const response: CheckoutResponse = await firstValueFrom(
        this.http.post<CheckoutResponse>(
          `${this.baseUrl}/purchase`,
          checkoutData,
        ),
      );
      if (response.status === 'success' && response.data?.approvalURl) {
        this.toastService.showSuccess(
          'Payment initiated! Redirecting to PayPal...',
        );
        sessionStorage.setItem(
          'checkoutData',
          JSON.stringify({
            ...checkoutData,
            purchaseId: response.data.purchaseId,
            paymentId: response.data.paymentId,
          }),
        );
        window.location.href = response.data.approvalURl; // Redirect to PayPal
      } else {
        this.toastService.showError(
          response.message || 'Failed to initiate payment.',
        );
      }
      return response;
    } catch (error: any) {
      console.error('Checkout error:', error);
      this.toastService.showError(
        'Checkout failed: ' + (error.error?.message || 'Something went wrong'),
      );
      throw error;
    }
  }
  // execute purchase payment
  async executePurchasePayment(
    checkoutData: CheckoutData,
    purchaseId: number,
    paymentId: string,
    payerId: string,
  ): Promise<CheckoutResponse> {
    debugger;
    const token = this.authService.getToken();
    if (!token) {
      throw new Error('No authentication token available');
    }

    const url = `${this.baseUrl}/purchase/${purchaseId}/payment/execute?paymentId=${paymentId}&PayerID=${payerId}`;
    console.log('Executing payment with URL:', url); // Debug log
    console.log('Payload:', checkoutData); // Debug log
    try {
      const response: CheckoutResponse = await firstValueFrom(
        this.http.post<CheckoutResponse>(url, checkoutData, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }),
      );
      if (response.status === 'success') {
        this.toastService.showSuccess('Payment completed successfully!');
        sessionStorage.removeItem('paymentDetails');
      }
      return response;
    } catch (error: any) {
      console.error('Payment execution error:', error);
      this.toastService.showError('Payment execution failed: ');
      throw error;
    }
  }

  // handle paypal cancel
  async cancelPurchase(purchaseId: number): Promise<CancelResponse> {
    const token = this.authService.getToken();
    if (!token) {
      this.toastService.showError('Token is missing. Please log in again.');
      throw new Error('No authentication token available');
    }

    const url = `${this.baseUrl}/purchase/${purchaseId}/cancel`;
    try {
      const response: CancelResponse = await firstValueFrom(
        this.http.post<CancelResponse>(
          url,
          {},
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          },
        ),
      );

      if (response.status === 'success') {
        this.toastService.showSuccess('Purchase cancelled successfully!');
      } else {
        this.toastService.showError(
          response.message || 'Failed to cancel purchase.',
        );
      }
      return response;
    } catch (error: any) {
      console.error('Cancel purchase error:', error);
      this.toastService.showError(
        'Failed to cancel purchase: ' +
          (error.error?.message || 'Something went wrong'),
      );
      throw error;
    }
  }
}

