import { Injectable } from '@angular/core';
import { CheckoutData } from 'app/customer/Model/checkOut';
import { TDocumentDefinitions } from 'pdfmake/interfaces';
import pdfMake from 'pdfmake/build/pdfmake';
import * as pdfFonts from 'pdfmake/build/vfs_fonts';

@Injectable({
  providedIn: 'root',
})
export class PdfService {
  private pdfDocGenerator: typeof pdfMake;
  constructor() {
    this.pdfDocGenerator = pdfMake;
    this.pdfDocGenerator.createPdf; // forces module to load correctly
    this.pdfDocGenerator.vfs = pdfFonts.vfs;
  }

  generateVoucherPdf(checkoutData: CheckoutData): pdfMake.TCreatedPdf {
    const documentDefinition: TDocumentDefinitions = {
      content: [
        // Header
        { text: 'Purchase Voucher', style: 'header' },
        {
          text: `Customer: ${checkoutData.username}`,
          style: 'subheader',
          margin: [0, 5, 0, 20],
        },

        // Purchase Details Section
        { text: 'Purchase Details', style: 'subheader', margin: [0, 0, 0, 10] },
        {
          table: {
            headerRows: 1,
            widths: ['*', 'auto', 'auto', 'auto', 'auto'],
            body: [
              ['Item Name', 'Barcode', 'Price', 'Quantity', 'Total'],
              ...checkoutData.cartItems.map((item) => [
                item.name,
                item.barcode,
                `$${item.discountedPrice.toFixed(2)}`,
                item.quantity.toString(), // Ensure consistent string type
                `$${(item.discountedPrice * item.quantity).toFixed(2)}`,
              ]),
            ],
          },
        },

        // Summary Section
        { text: 'Summary', style: 'subheader', margin: [0, 20, 0, 10] },
        {
          table: {
            widths: ['*', 'auto'],
            body: [
              ['Original Total', `$${checkoutData.originalAmount.toFixed(2)}`],
              ...(checkoutData.couponDiscount
                ? [
                    [
                      'Coupon Discount',
                      `-$${checkoutData.couponDiscount.toFixed(2)}`,
                    ],
                  ]
                : []),
              ...(checkoutData.loyaltyDiscount
                ? [
                    [
                      'Loyalty Discount',
                      `-$${checkoutData.loyaltyDiscount.toFixed(2)}`,
                    ],
                  ]
                : []),
              ...(checkoutData.discountAmount
                ? [
                    [
                      'Total Discount',
                      `-$${checkoutData.discountAmount.toFixed(2)}`,
                    ],
                  ]
                : []),
              ['Subtotal', `$${checkoutData.totalAmount.toFixed(2)}`],
              ['Tax', `$${checkoutData.taxAmount.toFixed(2)}`],
              [
                'Final Amount',
                { text: `$${checkoutData.finalAmount.toFixed(2)}`, bold: true },
              ],
            ],
          },
        },

        // Transaction Details Section
        {
          text: 'Transaction Details',
          style: 'subheader',
          margin: [0, 20, 0, 10],
        },
        {
          table: {
            widths: ['*', '*'],
            body: [
              ...(checkoutData.purchaseId
                ? [['Purchase ID', checkoutData.purchaseId.toString()]]
                : []),
              ...(checkoutData.paymentId
                ? [['Payment ID', checkoutData.paymentId]]
                : []),
              ...(checkoutData.payerId
                ? [['Payer ID', checkoutData.payerId]]
                : []),
              ...(checkoutData.couponCode
                ? [['Coupon Code', checkoutData.couponCode]]
                : []),
              ...(checkoutData.loyaltyPointsUsed
                ? [
                    [
                      'Loyalty Points Used',
                      checkoutData.loyaltyPointsUsed.toString(),
                    ],
                  ]
                : []),
              ...(checkoutData.pointsEarned !== undefined &&
              checkoutData.pointsEarned !== null
                ? [['Points Earned', checkoutData.pointsEarned.toString()]]
                : []),
              ...(checkoutData.transactionStatus
                ? [['Transaction Status', checkoutData.transactionStatus]]
                : []),
              ...(checkoutData.approvalURl
                ? [['Approval URL', checkoutData.approvalURl]]
                : []),
            ],
          },
        },
      ],
      styles: {
        header: {
          fontSize: 22,
          bold: true,
          alignment: 'center',
          margin: [0, 0, 0, 10],
        },
        subheader: { fontSize: 16, bold: true },
      },
      defaultStyle: { fontSize: 12 },
      pageSize: 'A4',
      footer: (currentPage: number, pageCount: number) => ({
        text: `Page ${currentPage} of ${pageCount}`,
        alignment: 'center',
        fontSize: 10,
        margin: [0, 10, 0, 0],
      }),
    };

    return pdfMake.createPdf(documentDefinition);
  }
}

import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root',
})
export class ProductService {
  private apiUrl = '/product';
  constructor(private http: HttpClient) {}

  getProductByBarcode(barcode: string): Observable<any> {
    const url = `${this.apiUrl}/search/${barcode}`;
    const response = this.http.get<any>(url);
    return response;
  }
}

import { Injectable } from '@angular/core';
import { ToastrService } from 'ngx-toastr';

@Injectable({
  providedIn: 'root',
})
export class ToastService {
  constructor(private toastr: ToastrService) {}
  showSuccess(message: string, title: string = 'Success'): void {
    this.toastr.success(message, title, {
      positionClass: 'toast-top-right',
      closeButton: false,
      timeOut: 5000,
      progressBar: true,
      extendedTimeOut: 2000, // Stay visible for 2 more seconds after hover
      tapToDismiss: true, // Dismiss on click
    });
  }

  showError(message: string, title: string = 'Error'): void {
    this.toastr.error(message, title, {
      positionClass: 'toast-top-right',
      closeButton: true,
      timeOut: 5000,
      progressBar: true,
      extendedTimeOut: 2000, // Stay visible for 2 more seconds after hover
      // tapToDismiss: true, // Dismiss on click
    });
  }

  showWarning(message: string, title: string = 'Warning'): void {
    this.toastr.warning(message, title, {
      positionClass: 'toast-top-right',
      closeButton: true,
      timeOut: 5000,
      progressBar: true,
      extendedTimeOut: 2000, // Stay visible for 2 more seconds after hover
      // tapToDismiss: true, // Dismiss on click
    });
  }

  showInfo(message: string, title: string = 'Info'): void {
    this.toastr.info(message, title, {
      positionClass: 'toast-top-right',
      closeButton: true,
      timeOut: 5000,
      progressBar: true,
      extendedTimeOut: 2000, // Stay visible for 2 more seconds after hover
      // tapToDismiss: true, // Dismiss on click
    });
  }
}

