/* General Reset */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

/* Container Styling */
.cart_container_fluid {
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
  background: #fff;
  font-family: "Arial", sans-serif;
}

/* Header */
.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 0;
  border-bottom: 1px solid #e0e0e0;
  margin-bottom: 20px;
}

.brand-logo {
  font-size: 1.5rem;
  font-weight: bold;
  color: #333;
}

.cart-icon {
  display: flex;
  align-items: center;
  gap: 5px;
}

.cart-count {
  background: #ff5a5f;
  color: #fff;
  border-radius: 50%;
  padding: 2px 6px;
  font-size: 0.8rem;
}

/* Stepper */
.stepper-container {
  margin-bottom: 20px;
}

mat-stepper {
  background: transparent;
}

/* Cart Items */
.cart-items {
  margin-bottom: 20px;
}

.cart-header {
  display: grid;
  grid-template-columns: 2fr 1fr 1fr 1fr 1fr 0.5fr;
  padding: 10px 0;
  border-bottom: 1px solid #e0e0e0;
  font-weight: bold;
  color: #333;
  text-transform: uppercase;
  font-size: 0.9rem;
}

.cart-item {
  display: grid;
  grid-template-columns: 2fr 1fr 1fr 1fr 1fr 0.5fr;
  align-items: center;
  padding: 15px 0;
  border-bottom: 1px solid #e0e0e0;
}

.item-details {
  display: flex;
  align-items: center;
  gap: 15px;
}

.item-image {
  width: 60px;
  height: 60px;
  object-fit: cover;
  border-radius: 4px;
}

.item-info {
  display: flex;
  flex-direction: column;
}

.item-name {
  font-size: 1rem;
  color: #333;
}

.item-subtext {
  font-size: 0.8rem;
  color: #777;
  text-decoration: underline;
  cursor: pointer;
}

.item-price,
.item-discount,
.item-total {
  font-size: 1rem;
  color: #333;
}

.price-text {
  font-weight: 500;
}

.strikethrough {
  text-decoration: line-through;
  color: #777;
}

.discount-text {
  color: #ff5a5f;
  font-weight: 500;
}

.item-quantity {
  display: flex;
  align-items: center;
  gap: 5px;
}

app-quantity-selector {
  display: flex;
  align-items: center;
  gap: 5px;
}

app-quantity-selector button {
  background: #f5f5f5;
  border: 1px solid #e0e0e0;
  border-radius: 50%;
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
}

app-quantity-selector span {
  width: 20px;
  text-align: center;
}

.item-actions {
  display: flex;
  justify-content: flex-end;
}

.remove-btn mat-icon {
  color: #777;
}

/* Pagination */
mat-paginator {
  background: transparent;
  padding: 10px 0;
  font-size: 0.9rem;
  color: #333;
}

/* Summary and Actions */
.cart-footer {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  gap: 20px;
  margin-top: 20px;
}

.summary-container {
  flex: 1;
  text-align: right;
}

.summary-row {
  display: flex;
  justify-content: flex-end;
  gap: 10px;
  margin-bottom: 10px;
}

.summary-label {
  font-size: 1.1rem;
  color: #333;
  font-weight: bold;
}

.summary-value {
  font-size: 1.2rem;
  color: #333;
  font-weight: bold;
}

.summary-note {
  font-size: 0.9rem;
  color: #777;
  margin-bottom: 10px;
}

.clear-cart-btn {
  background: transparent;
  color: #ff5a5f;
  border: 1px solid #ff5a5f;
  padding: 8px 16px;
  font-size: 0.9rem;
  border-radius: 4px;
  transition: all 0.3s ease;
}

.clear-cart-btn:hover {
  background: #ff5a5f;
  color: #fff;
}

/* Action Buttons */
.action-buttons {
  display: flex;
  gap: 10px;
}

.add-more-btn,
.checkout-btn {
  padding: 12px 20px;
  font-size: 1rem;
  border-radius: 4px;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 8px;
  text-transform: uppercase;
}

.add-more-btn {
  background: #f5f5f5;
  color: #333;
  border: 1px solid #e0e0e0;
}

.add-more-btn:hover {
  background: #e0e0e0;
}

.checkout-btn {
  background: #ff5a5f;
  color: #fff;
  border: none;
}

.checkout-btn:hover {
  background: #e04e53;
}

/* Disable future steps based on the current step */
mat-stepper[ng-reflect-selected-index="0"]
  .mat-step-header[ng-reflect-index="1"],
mat-stepper[ng-reflect-selected-index="0"]
  .mat-step-header[ng-reflect-index="2"],
mat-stepper[ng-reflect-selected-index="0"]
  .mat-step-header[ng-reflect-index="3"],
mat-stepper[ng-reflect-selected-index="1"]
  .mat-step-header[ng-reflect-index="2"],
mat-stepper[ng-reflect-selected-index="1"]
  .mat-step-header[ng-reflect-index="3"],
mat-stepper[ng-reflect-selected-index="2"]
  .mat-step-header[ng-reflect-index="3"] {
  opacity: 0.5; /* Visually indicate the step is disabled */
}

/* Ensure previous and current steps are clickable */
mat-stepper[ng-reflect-selected-index="1"]
  .mat-step-header[ng-reflect-index="0"],
mat-stepper[ng-reflect-selected-index="2"]
  .mat-step-header[ng-reflect-index="0"],
mat-stepper[ng-reflect-selected-index="2"]
  .mat-step-header[ng-reflect-index="1"],
mat-stepper[ng-reflect-selected-index="3"]
  .mat-step-header[ng-reflect-index="0"],
mat-stepper[ng-reflect-selected-index="3"]
  .mat-step-header[ng-reflect-index="1"],
mat-stepper[ng-reflect-selected-index="3"]
  .mat-step-header[ng-reflect-index="2"] {
  pointer-events: auto;
  opacity: 1;
}
/* Responsive Adjustments */
@media (max-width: 768px) {
  .cart_container_fluid {
    padding: 15px;
  }

  .cart-header,
  .cart-item {
    grid-template-columns: 2fr 1fr 1fr 1fr 1fr 0.5fr;
  }

  .item-image {
