import {
  AfterViewInit,
  Component,
  ElementRef,
  inject,
  OnInit,
  ViewChild,
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatDialog, MatDialogModule } from '@angular/material/dialog';
import { LoginModalComponent } from '../login-modal/login-modal.component';
import { MatButton } from '@angular/material/button';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { MatIconModule } from '@angular/material/icon';
import { AuthService } from 'app/Service/auth/auth.service';
import { Route, Router, RouterModule } from '@angular/router';
import { ConfirmComponent } from 'app/Common/dialog/confirm/confirm.component';
import { MatMenuModule } from '@angular/material/menu';
import { MatDividerModule } from '@angular/material/divider';

@Component({
  selector: 'app-header',
  imports: [
    CommonModule,
    MatDialogModule,
    MatButton,
    FontAwesomeModule,
    MatIconModule,
    RouterModule,
    MatMenuModule,
    MatDividerModule,
  ],
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css'],
})
export class HeaderComponent implements OnInit, AfterViewInit {
  user: any = null;
  logoPath: string = 'assets/logo.png';
  @ViewChild('tabNav') tabNav!: ElementRef; // Reference to the nav element

  constructor(
    private dialog: MatDialog,
    private authService: AuthService,
    private router: Router,
  ) {}
  ngOnInit(): void {
    // Subscribe to user data changes
    this.authService.user$.subscribe((userData) => {
      this.user = userData;
    });
  }

  ngAfterViewInit(): void {
    this.updateIndicator();
    // Listen to route changes or click events to update indicator
    const links = this.tabNav.nativeElement.querySelectorAll('.tab-link');
    links.forEach((link: HTMLElement) => {
      link.addEventListener('click', () => this.updateIndicator());
    });
  }

  updateIndicator(): void {
    const activeLink =
      this.tabNav.nativeElement.querySelector('.tab-link.active');
    const indicator = this.tabNav.nativeElement.querySelector('.tab-indicator');
    if (activeLink && indicator) {
      const linkRect = activeLink.getBoundingClientRect();
      const navRect = this.tabNav.nativeElement.getBoundingClientRect();
      indicator.style.width = `${linkRect.width}px`;
      indicator.style.left = `${linkRect.left - navRect.left}px`;
    }
  }

  openLoginModel() {
    // Open the login model
    this.dialog.open(LoginModalComponent, {
      width: '50%',
      height: 'auto', // or a specific height
      maxHeight: '80vh',
      exitAnimationDuration: '1000ms',
      enterAnimationDuration: '1000ms',
    });
  }
  logout(): void {
    const dialogRef = this.dialog.open(ConfirmComponent, {
      width: '350px',
      data: {
        title: 'Logout',
        message: 'Are you sure you want to log out?',
      },
    });
    dialogRef.afterClosed().subscribe((result: boolean) => {
      if (result) {
        // User confirmed, proceed with logout
        this.authService.logout();
        this.router.navigate(['/home']);
      }
    });
  }
  debugClick(event: Event) {
    console.log('Cart link clicked!');
  }
}

