    MatCardModule,
    ReactiveFormsModule,
    MatInputModule,
    MatButtonModule,
    MatFormFieldModule,
    MatDatepickerModule,
    MatSelectModule,
    MatIconModule,
    CommonModule,
    MatDialogModule,
  ],
  templateUrl: './login-modal.component.html',
  styleUrls: ['./login-modal.component.css'],
})
export class LoginModalComponent implements OnInit {
  hide = true;
  loginForm: any;
  title = 'Login';
  constructor(
    private ref: MatDialogRef<LoginModalComponent>,
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router,
    private dialog: MatDialog,
    private toastService: ToastService
  ) {}
  ngOnInit(): void {
    this.loginForm = this.fb.group({
      username: ['', [Validators.required]],
      password: ['', [Validators.required]],
    });
  }

  // Login
  loginSubmit() {
    if (this.loginForm.valid) {
      const loginData: Login = this.loginForm.value;
      // Call the service method to handle login
      this.authService.login(loginData.username, loginData.password).subscribe({
        next: (response: any) => {
          // console.log('Login successful:', response);
          this.toastService.showSuccess('Login successful');
          this.ref.close();
          this.router.navigate(['home']);
        },
        error: (error: any) => {
          let errorMessage = 'An unknown error occurred.';
          if (error.error instanceof ErrorEvent) {
            errorMessage = `Client-side error: ${error.error.message}`;
          } else {
            // Backend error
            errorMessage = error.error?.message || error.statusText;
            // Optionally modify the error message
            if (errorMessage === 'Invalid username or password.') {
              errorMessage =
                'The username or password you entered is incorrect. Please try again.';
            }
          }
          this.toastService.showError(errorMessage); // Show error toast
        },
      });
    }
  }
  closeModel() {
    this.ref.close();
  }

  openRegisterModal() {
    // Close the login dialog
    this.ref.close();
    // Open the Register dialog
    this.dialog.open(RegisterModalComponent, {
      width: '50%',
      height: 'auto',
      exitAnimationDuration: '1000ms',
      enterAnimationDuration: '1000ms',
    });
  }
}

