import { Component, Inject, OnInit } from '@angular/core';
import { MatButtonModule } from '@angular/material/button';
import { MatCardModule } from '@angular/material/card';
import {
  MAT_DIALOG_DATA,
  MatDialog,
  MatDialogModule,
  MatDialogRef,
} from '@angular/material/dialog';
import { CommonModule } from '@angular/common';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { MatInputModule } from '@angular/material/input';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatDatepickerModule } from '@angular/material/datepicker';
import { MatSelectModule } from '@angular/material/select';
import { MatIconModule } from '@angular/material/icon';
import { AuthService } from 'app/Service/auth/auth.service';
import { Router } from '@angular/router';
import { ToastService } from 'app/Service/toast/toast.service';

@Component({
  selector: 'app-otp-verficiation',
  imports: [
    MatCardModule,
    ReactiveFormsModule,
    MatInputModule,
    MatButtonModule,
    MatFormFieldModule,
    MatDatepickerModule,
    MatSelectModule,
    MatIconModule,
    CommonModule,
    MatDialogModule,
  ],
  templateUrl: './otp-verficiation.component.html',
  styleUrls: ['./otp-verficiation.component.css'],
})
export class OtpVerificationComponent implements OnInit {
  otpForm!: FormGroup;

  constructor(
    private fb: FormBuilder,
    private authService: AuthService,
    private router: Router,
    private ref: MatDialogRef<OtpVerificationComponent>,
    private toastService: ToastService,
    @Inject(MAT_DIALOG_DATA)
    public data: {
      username: string;
      email: string;
      password: string;
    }
  ) {}

  ngOnInit(): void {
    this.initForm();
    console.log('Data received in OTP modal:', this.data);
  }

  private initForm(): void {
    this.otpForm = this.fb.group({
      otp: ['', [Validators.required, Validators.pattern('^[0-9]{4}$')]],
    });
  }

  get otp() {
    return this.otpForm.get('otp'); //  Getter for easy access in template
  }

  onVerifyOtp(): void {
    if (this.otpForm.valid) {
      console.log(this.data);
      const otpData = {
        username: this.data.username, // Use the passed username
        email: this.data.email, // Use the passed email
        password: this.data.password, // Use the passed password
        otp: this.otpForm.value.otp, // OTP entered by the user
      };
      console.log(otpData);
      this.authService.verifyOtp(otpData, otpData.otp).subscribe({
        next: (response) => {
          if (response.status === 'success') {
            this.toastService.showSuccess('OTP Verified Successfully!');
            this.ref.close('verified');
            // this.router.navigate(['home']);
          } else {
            this.toastService.showError('Invalid OTP. Please try again.');
          }
        },
        error: (error) => {
          console.error('OTP Verification Failed:', error);
          this.toastService.showError('An error occurred while verifying OTP.');
        },
      });
    } else {
      this.otpForm.markAllAsTouched();
    }
  }
}

