  // }

  async executePayment(): Promise<void> {
    if (!this.purchaseId || !this.paymentId || !this.payerId) {
      this.toastService.showError('Missing payment parameters');
      return;
    }

    try {
      const storedCheckoutItems = sessionStorage.getItem('checkoutData');
      if (!storedCheckoutItems) {
        throw new Error('Checkout items not found in session storage.');
      }
      const checkoutItems: any = JSON.parse(storedCheckoutItems);

      const checkoutData = {
        purchaseId: Number(this.purchaseId),
        cartItems: checkoutItems.cartItems.map((item: any) => ({
          barcode: item.barcode,
          name: item.name || item.productName,
          price: item.price,
          discountedPrice: item.discountedPrice,
          quantity: item.quantity || item.cartQuantity,
        })),
        originalAmount: checkoutItems.originalAmount,
        totalAmount: checkoutItems.totalAmount,
        couponCode: checkoutItems.couponCode,
        couponDiscount: checkoutItems.couponDiscount,
        loyaltyPointsUsed: checkoutItems.loyaltyPointsUsed,
        loyaltyDiscount: checkoutItems.loyaltyDiscount,
        taxAmount: checkoutItems.taxAmount,
        finalAmount: checkoutItems.finalAmount,
        username: checkoutItems.username,
      };

      const response = await this.checkoutService.executePurchasePayment(
        checkoutData,
        Number(this.purchaseId),
        this.paymentId,
        this.payerId,
      );

      if (response.status === 'success') {
        this.toastService.showSuccess('Payment completed successfully!');
        sessionStorage.removeItem('checkoutData');

        //Update checkout data with response data
        this.checkoutData = { ...checkoutData, ...response.data };

        this.paymentExecuted.emit(this.checkoutData); // Emit purchase data
      } else {
        this.toastService.showError('Payment execution failed.');
      }
    } catch (error) {
      console.error('Error executing payment:', error);
      this.toastService.showError('Failed to process payment.');
    }
  }

  private getCookie(name: string): string | null {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop()?.split(';').shift() || null;
    return null;
  }
  private async loadCheckoutData(): Promise<void> {
    const storedCheckout = sessionStorage.getItem('checkoutData');
    if (storedCheckout) {
      this.checkoutData = JSON.parse(storedCheckout);
    }
  }
  async cancelPayment(): Promise<void> {
    if (!this.purchaseId) {
      this.toastService.showError('Missing purchase ID for cancellation');
      return;
    }

    try {
      const response = await this.checkoutService.cancelPurchase(
        Number(this.purchaseId),
      );
      if (response.status === 'success') {
        sessionStorage.removeItem('checkoutData'); // Clear checkout data
        this.paymentCancelled.emit(); // Notify parent component
      }
    } catch (error) {
      console.error('Error cancelling payment:', error);
      this.toastService.showError('Failed to cancel payment.');
    }
  }
}

import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ProductService } from '../../../apiService/productService/product.service';
import { ToastService } from '../../../../Service/toast/toast.service';
import { CommonModule } from '@angular/common';
import { CartService } from 'app/customer/apiService/cartService/cart.service';
import { HttpClient } from '@angular/common/http';
import { firstValueFrom } from 'rxjs';
import { MainService } from 'app/Service/main.service';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-product-detail',
  imports: [CommonModule, FormsModule],
  templateUrl: './product-detail.component.html',
  styleUrl: './product-detail.component.css',
})
export class ProductDetailComponent implements OnInit {
  product: any;
  barcode: string | null = null;
  loading: boolean = false;
  errorMessage: string = '';
  quantity: number = 1;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private productService: ProductService,
    private toastService: ToastService,
    private cartService: CartService,
    private http: HttpClient,
    private mainService: MainService
  ) {}

  ngOnInit(): void {
    this.showProductDetail();
  }

  showProductDetail(): void {
    // Get product details from history.state (only on navigation via router.navigate)
    if (history.state && history.state.productDetails) {
      this.product = history.state.productDetails.data; // Use passed product details if available
    } else {
      // If product details not passed, fetch it from API based on barcode
      this.route.params.subscribe((params) => {
        if (params['barcode']) {
          this.barcode = params['barcode'];
          if (this.barcode) {
            this.fetchProductByBarcode(this.barcode); // Make API call if barcode is present
          }
        }
      });
    }
  }

  fetchProductByBarcode(barcode: string): void {
    this.loading = true;
    this.productService.getProductByBarcode(barcode).subscribe({
      next: (response: any) => {
        if (response.status === 'success' && response.data) {
          // console.log('Product details:', response.data);
          this.product = response.data; // Update product details with API response
          this.loading = false;
        } else {
          console.log('Error fetching product:', response.message);
          this.loading = false;
          this.toastService.showError('Product not found!');
        }
      },
      error: (error: any) => {
        console.error('Api error:', error);
        this.goBack(); // Handle error and navigate back if API call fails
      },
    });
  }

  goBack(): void {
    this.router.navigate(['/scan']);
  }

  // add to cart
  async addItemToCart(product: any, quantity: number): Promise<void> {
    try {
      // Check if the user is logged in
      if (!this.mainService.isUserLoggedIn()) {
        this.toastService.showWarning(
          'Please log in to add items to your cart.'
        );
        return;
      }

      // Add product to cart using CartService
      await this.cartService.addToCart(product, quantity);

      // Redirect to cart page
      this.router.navigate(['/cart']);
    } catch (error) {
      console.error('Error adding to cart:', error);
      this.toastService.showError('An error occurred while adding to cart.');
    }
  }
}

import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-quantity-selector',
  imports: [],
  templateUrl: './quantity-selector.component.html',
  styleUrls: ['./quantity-selector.component.css'],
})
export class QuantitySelectorComponent {
  // Input property to set the initial quantity
  @Input() count: number = 0;

  // Output event to notify parent components of changes
  @Output() countChange = new EventEmitter<number>();
  increment() {
    this.count++;
    this.countChange.emit(this.count);
  }
  decrement() {
    if (this.count > 0) {
      this.count--;
      this.countChange.emit(this.count);
    }
  }
}

