.sidebar-wrapper {
  width: 220px; /* Slightly more than label content */
  height: 100vh;
  top: 0;
  left: 0;
  overflow: hidden;
  border-top-right-radius: 20px;
  border-bottom-right-radius: 20px;
  border-right: none;
}

.nav-tabs {
  position: relative;
  height: 100%;
  display: flex;
  flex-direction: column;
  --tab-count: 6; /* Default value, overridden by the component */
}
/* Icon and text alignment */
.tab-label mat-icon {
  margin-right: 8px;
}
.tab-label {
  margin: 20px 0 10px 0;
  flex: 1; /* Each label takes equal height */
  font-size: 18px;
  font-weight: 700;
  text-align: left;
  color: #000000;
  opacity: 0.5;
  cursor: pointer;
  -webkit-user-select: none; /* Safari support */
  user-select: none;
  transition:
    opacity 0.4s ease-in-out,
    color 0.4s ease-in-out;
  z-index: 2;
  position: relative;
  padding: 0 10px; /* Some horizontal padding */
  box-sizing: border-box; /* Prevent overflow */
}

.marker {
  position: absolute;
  width: 100%;
  height: 200%;
  display: flex;
  flex-direction: column;
  top: calc(-100%);
  left: 0;
  transition: transform 0.2s ease-in-out;
  transform: translateY(
    calc(calc(50% / var(--tab-count)) * var(--active-index))
  );
  z-index: 1; /* Lower than .tab-label */
}

.tab-label.active {
  opacity: 1;
  color: var(--gray-900);
}

.marker #top,
.marker #bottom {
  background-color: #dfe6ed;
  /* box-shadow: 32px 32px 48px #2e364315; */
}

.marker #top {
  height: calc(50%);
  margin-bottom: auto;
  border-radius: 0 0 32px 0;
}

.marker #bottom {
  height: calc(50% - 72px);
  border-radius: 0 32px 0 0;
}
.tab-list {
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow-x: hidden; /* Prevent horizontal scroll */
  overflow-y: auto; /* Still allows vertical scrolling if needed */
  transition: justify-content 0.3s ease;
}

.center-tabs {
  justify-content: center; /* Vertically center tabs */
}
/* Expand icon */
.expand-icon {
  transition: transform 0.3s ease;
}

.expand-icon.expanded {
  transform: rotate(180deg);
}

/* Child tab container */
.children-container {
  margin-left: 20px; /* Indent child tabs */
  padding-top: 5px;
}

/* Child tab styling */
.child-tab {
  cursor: pointer;
  padding: 8px 16px;
  display: flex;
  align-items: center;
  color: #333;
  font-size: 12px; /* Smaller font size for child tabs */
  transition: background-color 0.3s ease;
  height: 32px; /* Fixed height for child tabs */
}

.child-tab:hover {
  background-color: #d3d9de; /* Highlight on hover */
}

.child-tab.active {
  background-color: #d3d9de; /* Highlight active child tab */
}

.child-tab mat-icon {
  margin-right: 8px;
}

