import { CommonModule } from '@angular/common';
import { Component, Input } from '@angular/core';
import { CheckoutData } from 'app/customer/Model/checkOut';
import { PdfService } from 'app/Service/pdf/pdf.service';

@Component({
  selector: 'app-voucher',
  imports: [CommonModule],
  templateUrl: './voucher.component.html',
  styleUrl: './voucher.component.css',
})
export class VoucherComponent {
  @Input() checkoutData!: CheckoutData; // Required input
  pdfUrl: string | null = null;

  constructor(private pdfService: PdfService) {}

  ngOnInit(): void {
    this.generateVoucher();
  }

  generateVoucher(): void {
    if (this.checkoutData) {
      const pdf = this.pdfService.generateVoucherPdf(this.checkoutData);
      pdf.getDataUrl((dataUrl: string) => {
        this.pdfUrl = dataUrl;
      });
    }
  }

  downloadVoucher(): void {
    if (this.checkoutData) {
      this.pdfService
        .generateVoucherPdf(this.checkoutData)
        .download('voucher.pdf');
    }
  }
}

